mapper.select:
SELECT IFNULL(LMB.FIRST_NAME, TCM.FIRST_NAME) AS FIRST_NAME, IFNULL(LMB.LAST_NAME, TCM.LAST_NAME) AS LAST_NAME, IFNULL(LMB.BUSINESS_NAME, TCM.BUSINESS_NAME) AS BUSINESS_NAME, IFNULL(LMB.MEMBER_TYPE, TCM.CUSTOMER_TYPE) AS MEMBER_TYPE, IFNULL(LMB.TITLE, TCM.TITLE) AS TITLE, IFNULL(LMB.PHONE_NO, TCM.PHONE_NO) AS PHONE_NO, IFNULL(LMB.EMAIL, TCM.EMAIL) AS EMAIL, LC.CASE_NUMBER, LC.DETAIL, LC.TYPE, LC.SUBTYPE, LC.PRIORITY, LC.STATUS, LC.CHANNEL, LC.OWNER, LC.SUBJECT, LC.OPENED_DATE, LC.CLOSED_DATE, LC.BU_ID, LC.CUSTOMER_ID, LC.CREATED_DATE, LC.CREATED_BY, LC.UPDATED_DATE, LC.UPDATED_BY, CONCAT(USR.FIRST_NAME,' ',USR.LAST_NAME) AS displayName, CB1.CODE_NAME AS typeName, CB2.CODE_NAME AS subTypeName, CB3.CODE_NAME AS priorityName FROM TB_CASE LC INNER JOIN TB_USER USR ON LC.OWNER = USR.ID INNER JOIN TB_CODEBOOK CB1 ON LC.TYPE = CB1.CODE_ID AND CB1.CODE_TYPE = 'CASE_TYPE' INNER JOIN TB_CODEBOOK CB2 ON LC.SUBTYPE = CB2.CODE_ID AND CB2.CODE_TYPE = 'CASE_SUBTYPE' INNER JOIN TB_CODEBOOK CB3 ON LC.PRIORITY = CB3.CODE_ID AND CB3.CODE_TYPE = 'CASE_PRIORITY' INNER JOIN TB_CUSTOMER TCM ON LC.CUSTOMER_ID = TCM.CUSTOMER_ID LEFT JOIN LOY_MEMBER LMB ON LC.CUSTOMER_ID = LMB.CUSTOMER_ID
AND LC.CASE_NUMBER = #{caseNumber}
AND LC.TYPE = #{type}
AND LC.SUBTYPE = #{subType}
AND LC.BU_ID = #{buId}
AND LC.STATUS = #{status}
AND LC.CUSTOMER_ID = #{customerId}
AND LC.OWNER = #{ownerId}
AND DATE(LC.OPENED_DATE) >= #{openedDateStart, jdbcType=DATE}
AND DATE(LC.OPENED_DATE) <= #{openedDateEnd, jdbcType=DATE}
AND DATE(LC.CLOSED_DATE) >= #{closedDateStart, jdbcType=DATE}
AND DATE(LC.CLOSED_DATE) <= #{closedDateEnd, jdbcType=DATE}
ORDER BY LC.CREATED_DATE DESC,LC.CASE_NUMBER DESC

	call GEN_NEW_SEQUENCE(
		'CASE_SEQUENCE',
  	#{buId, jdbcType = INTEGER, mode = IN}
	)
