mapper.select:
SELECT m.`MEMBER_ID`, m.`CUSTOMER_ID`, m.`MEMBER_TYPE`, m.`MEMBER_STATUS`, m.`CITIZEN_ID`, m.`PASSPORT_NO`, m.`TITLE`, m.`FIRST_NAME`, m.`LAST_NAME`, m.`BUSINESS_NAME`, m.`BUSINESS_TYPE`, m.`GENDER`, m.`NATIONALITY`, m.`BIRTHDATE`, m.`OCCUPATION`, m.`MARITAL_STATUS`, m.`IMAGE_URL`, m.`APPROVED_DATE`, CONCAT(COALESCE(u1.FIRST_NAME,''), ' ',COALESCE(u1.LAST_NAME,'')) AS APPROVED_BY_NAME, m.`REGISTRATION_STORE`, m.`REGISTRATION_CHANNEL`, m.`REMARK`, m.`BU_ID`, m.`CREATED_DATE`, CONCAT(COALESCE(u1.FIRST_NAME,''), ' ',COALESCE(u1.LAST_NAME,'')) AS createdByName, m.`UPDATED_DATE`, CONCAT(COALESCE(u2.FIRST_NAME,''), ' ',COALESCE( u2.LAST_NAME,'')) AS updatedByName, m.`PHONE_AREA`, m.`PHONE_NO`, m.`EMAIL`, m.`PROGRAM_ID`, m.`MEMBER_TIER_ID`, m.`TAX_ID` FROM loy_member m LEFT JOIN TB_USER u1 ON m.CREATED_BY = u1.ID LEFT JOIN TB_USER u2 ON m.UPDATED_BY = u2.ID LEFT OUTER JOIN TB_USER a1 ON m.APPROVED_BY = a1.ID
AND m.`MEMBER_ID` = #{memberId}
AND m.`CUSTOMER_ID` = #{customerId}

mapper.update:
update loy_member 
SET 
 `BUSINESS_NAME`=#{businessName}
 ,`BUSINESS_TYPE`=#{businessType}
 ,`BIRTHDATE`=#{birthDate}
 ,`CITIZEN_ID`=#{citizenId}
 ,`TAX_ID`=#{taxId}
 ,`EMAIL`=#{email}
 ,`FIRST_NAME`=#{firstName}
 ,`GENDER`=#{gender}
 ,`LAST_NAME`=#{lastName}
 ,`MARITAL_STATUS`=#{maritalStatus}
 ,`NATIONALITY`=#{nationality}
 ,`OCCUPATION`=#{occupation}
 ,`PASSPORT_NO`=#{passportNo}
 ,`PHONE_AREA`=#{phoneArea}
 ,`PHONE_NO`=#{phoneNo}
 ,`REMARK`=#{remark}
 ,`TITLE`=#{title}
 , UPDATED_BY = #{updatedBy}
 , UPDATED_DATE = now()
where `MEMBER_ID` = #{memberId}

mapper.insert:
INSERT INTO loy_member_address (`MEMBER_ID`, `ADDRESS`, `ADDRESS_TYPE`, `PRIMARY_YN`, `SUB_DISTRICT`, `DISTRICT`, `PROVINCE`, `COUNTRY`, `POSTCODE`, `BU_ID`, `CREATED_DATE`, `CREATED_BY`, `UPDATED_DATE`, `UPDATED_BY`)
SELECT  #{memberId}, `ADDRESS`, `ADDRESS_TYPE`, `PRIMARY_YN`, `SUB_DISTRICT`, `DISTRICT`, `PROVINCE`, `COUNTRY`, `POSTCODE`, `BU_ID`, NOW(), #{updatedBy}, NOW(), #{updatedBy}
FROM tb_customer_address WHERE `CUSTOMER_ID` = #{customerId}

mapper.select:
select a.*, concat( a.`ADDRESS`,' ',ifnull(t.`SUB_DISTRICT`,''),' ',ifnull(t.`DISTRICT`,''),' ',ifnull(t.`PROVINCE`,'')) as fullAddress
from loy_member_address a
left join tb_province t on a.`SUB_DISTRICT`=t.`LOCATION_CODE`


		AND a.`MEMBER_ID` = #{memberId}


		AND a.BU_ID = #{buId}

select a.*, concat( a.`ADDRESS`,' ',ifnull(t.`SUB_DISTRICT`,''),' ',ifnull(t.`DISTRICT`,''),' ',ifnull(t.`PROVINCE`,'')) as fullAddress
from loy_member_address a
left join tb_province t on a.`SUB_DISTRICT`=t.`LOCATION_CODE`



    		AND a.ADDRESS_ID =  #{addressId}



    		AND a.PRIMARY_YN = #{primaryYn}



    		AND a.MEMBER_ID = #{memberId}


		AND a.BU_ID = #{buId}

select a.*, b.`TIER_NAME`
from loy_member_card a
left join loy_tier b on a.`CARD_TIER_ID` = b.`TIER_ID`


		AND a.`MEMBER_ID` = #{memberId}


		AND a.`PRIMARY_YN` = #{primaryYn}


ORDER BY a.PRIMARY_YN desc,a.CREATED_DATE desc

mapper.update:
UPDATE loy_member_att 
SET
	TITLE = #{title}
	, DESCP = #{descp}
	, ATT_ID = #{attId}
	, MAIN_FLAG = #{mainFlag}
	, UPDATED_BY = #{updatedBy}
	, UPDATED_DATE = now()
WHERE
	MEMBER_ATT_ID  = #{memberAttId}

mapper.select:
SELECT LC.CASE_NUMBER, LC.DETAIL, LC.TYPE, LC.SUBTYPE, LC.PRIORITY, LC.STATUS, LC.CHANNEL, LC.OWNER, LC.SUBJECT, LC.OPENED_DATE as openedDateDate, LC.CLOSED_DATE as closedDateDate, LC.BU_ID, LC.CUSTOMER_ID, CB1.CODE_NAME AS typeName, CB2.CODE_NAME AS subTypeName, CB3.CODE_NAME AS priorityName, CB4.CODE_NAME AS channelName, CB5.CODE_NAME AS statusName, CONCAT(IFNULL(U.FIRST_NAME,''),' ',IFNULL(U.LAST_NAME,'')) displayName FROM LOY_CASE LC INNER JOIN TB_CODEBOOK CB1 ON LC.TYPE = CB1.CODE_ID AND CB1.CODE_TYPE = 'CASE_TYPE' INNER JOIN TB_CODEBOOK CB2 ON LC.SUBTYPE = CB2.CODE_ID AND CB2.CODE_TYPE = 'CASE_SUBTYPE' INNER JOIN TB_CODEBOOK CB3 ON LC.PRIORITY = CB3.CODE_ID AND CB3.CODE_TYPE = 'CASE_PRIORITY' INNER JOIN TB_CODEBOOK CB4 ON LC.CHANNEL = CB4.CODE_ID AND CB4.CODE_TYPE = 'CASE_CHANNEL' INNER JOIN TB_CODEBOOK CB5 ON LC.STATUS = CB5.CODE_ID AND CB5.CODE_TYPE = 'CASE_STATUS' LEFT JOIN tb_user U ON LC.OWNER = U.ID
AND LC.CUSTOMER_ID = #{customerId}
AND LC.BU_ID = #{buId}
ORDER BY LC.CASE_NUMBER DESC

mapper.update:
update jlo.loy_member_card
set CARD_STATUS = '04'
, CARD_LAST_BLOCK_DATE = now()
, CARD_BLOCK_REASON = #{cardBlockReason}
, UPDATED_BY = #{updatedBy}
, UPDATED_DATE = now()
where MEMBER_CARD_NO = #{memberCardNo}

mapper.select:
 select t.TIER_ID as code_id
 ,TIER_NAME as code_name
 from jlo.loy_tier t 
 where t.PROGRAM_ID = (
	select m.PROGRAM_ID from jlo.loy_member m
	where m.MEMBER_ID = #{memberId}
) and t.TIER_ACTIVE_FLAG = 'Y'
order by t.TIER_NAME;
