mapper.select:
SELECT CB.`CODE_ID`, CB.`CODE_TYPE`, CB.`CODE_NAME`, CB.`PARENT_TYPE`, CB.`PARENT_ID`, CB.`LANG`, CB.`SEQ`, CB.`ETC1`, CB.`ETC2`, CB.`ETC3`, CB.`ETC4`, CB.`ETC5`, CB.`DESCRIPTION`, CB.`ACTIVE_FLAG`, CB.`SYNC_FLAG`, CB.`BU_ID`, CB.`CREATED_DATE`, CB.`CREATED_BY`, CB.`UPDATED_DATE`, CB.`UPDATED_BY`, CONCAT(COALESCE(u1.FIRST_NAME,''), ' ',COALESCE(u1.LAST_NAME,'')) AS createdByName, CONCAT(COALESCE(u2.FIRST_NAME,''), ' ',COALESCE( u2.LAST_NAME,'')) AS updatedByName
FROM tb_codebook CB LEFT JOIN TB_USER u1 ON CB.CREATED_BY = u1.ID LEFT JOIN TB_USER u2 ON CB.UPDATED_BY = u2.ID
WHERE CB.CODE_TYPE = 'PROMPT_METHOD'
AND CB.CODE_ID like #{pCodeId}
AND CB.CODE_NAME like #{pCodeName}
AND CB.ACTIVE_FLAG = #{activeFlag}
order by CB.CODE_TYPE ${sortDirection}
order by CB.CODE_ID ${sortDirection}
order by CB.CODE_NAME ${sortDirection}
order by CB.CODE_TYPE ASC

SELECT * FROM TB_CODEBOOK
CB
WHERE CODE_TYPE = 'PROMPT_METHOD' AND CODE_ID = #{codeId}

mapper.update:
UPDATE TB_CODEBOOK
SET
CODE_NAME = #{codeName}
, PARENT_TYPE = #{parentType}
, PARENT_ID = #{parentId}
, ETC1 = #{etc1}
, ETC2 = #{etc2}
, ETC3 = #{etc3}
, ETC4 = #{etc4}
, ETC5 = #{etc5}
, SEQ = #{seq}
, DESCRIPTION = #{description}
, ACTIVE_FLAG = #{activeFlag}
, UPDATED_BY = #{updatedBy}
, UPDATED_DATE = NOW()
WHERE
CODE_TYPE = 'PROMPT_METHOD'
AND CODE_ID = #{codeId}
AND BU_ID = #{buId}
