mapper.select:
SELECT TCM.FIRST_NAME AS FIRST_NAME, TCM.LAST_NAME AS LAST_NAME, TCM.BUSINESS_NAME AS BUSINESS_NAME, TCM.CUSTOMER_TYPE AS MEMBER_TYPE, TCM.TITLE AS TITLE, TCM.PHONE_NO AS PHONE_NO, TCM.EMAIL AS EMAIL, LC.SR_NUMBER as srNumber, LC.DETAIL, LC.TYPE, LC.SUBTYPE, LC.PRIORITY, LC.STATUS, LC.CHANNEL, LC.OWNER, LC.SUBJECT, LC.DUE_DATE, LC.OPENED_DATE, LC.CLOSED_DATE, LC.BU_ID, LC.CUSTOMER_ID, LC.CREATED_DATE, LC.CREATED_BY, LC.UPDATED_DATE, LC.UPDATED_BY, CONCAT(COALESCE(u1.FIRST_NAME, ''), ' ', COALESCE(u1.LAST_NAME, '')) AS createdByName, CONCAT(COALESCE(u2.FIRST_NAME, ''), ' ', COALESCE(u2.LAST_NAME, '')) AS updatedByName, CONCAT(USR.FIRST_NAME, ' ', USR.LAST_NAME) AS displayName, CB1.CODE_NAME AS typeName, CB2.CODE_NAME AS subTypeName, CB3.CODE_NAME AS priorityName, CB4.CODE_NAME AS divisionTypeName, LC.DIVISION_TYPE_CODE as divisionTypeCode, LC.SERVICE_AREA_CODE as serviceAreaCode, LC.WORK_NOTE as workNote, LC.INFORM_NAME as informName
FROM TB_SR LC INNER JOIN TB_USER USR ON LC.OWNER = USR.ID LEFT OUTER JOIN TB_CODEBOOK CB1 ON LC.TYPE = CB1.CODE_ID AND CB1.CODE_TYPE = 'SR_TYPE' LEFT OUTER JOIN TB_CODEBOOK CB2 ON LC.SUBTYPE = CB2.CODE_ID AND CB2.CODE_TYPE = 'SR_SUBTYPE' LEFT OUTER JOIN TB_CODEBOOK CB3 ON LC.PRIORITY = CB3.CODE_ID AND CB3.CODE_TYPE = 'SR_PRIORITY' LEFT OUTER JOIN TB_CODEBOOK CB4 ON LC.DIVISION_TYPE_CODE = CB4.CODE_ID AND CB4.CODE_TYPE = 'SR_DIVISION' INNER JOIN TB_CUSTOMER TCM ON LC.CUSTOMER_ID = TCM.CUSTOMER_ID LEFT JOIN TB_USER u1 ON LC.CREATED_BY = u1.ID LEFT JOIN TB_USER u2 ON LC.UPDATED_BY = u2.ID
AND LC.SR_NUMBER = #{srNumber}
AND LC.TYPE = #{type}
AND LC.SUBTYPE = #{subType}
AND LC.BU_ID = #{buId}
AND LC.STATUS = #{status}
AND LC.CUSTOMER_ID = #{customerId}
AND LC.OWNER = #{ownerId}
AND DATE(LC.OPENED_DATE) >= #{openedDateStart, jdbcType=DATE}
AND DATE(LC.OPENED_DATE) <= #{openedDateEnd, jdbcType=DATE}
AND DATE(LC.CLOSED_DATE) >= #{closedDateStart, jdbcType=DATE}
AND DATE(LC.CLOSED_DATE) <= #{closedDateEnd, jdbcType=DATE}
ORDER BY LC.CREATED_DATE DESC,LC.SR_NUMBER DESC

call GEN_NEW_SEQUENCE(
'SR_SEQUENCE',
#{buId, jdbcType = INTEGER, mode = IN}
)
