mapper.insert:
INSERT INTO tb_wf_task
(
 TASK_NAME
, WORKFLOW_ID
, DESCRIPTION
, STATUS
, PRIORITY
, START_DATE
, END_DATE
, BU_ID
, CREATED_DATE
, CREATED_BY
, UPDATED_DATE
, UPDATED_BY
)
VALUES(
 #{taskName}
, #{workflowId}
, #{description}
, #{status}
, #{priority}
, #{startDate}
, #{endDate}
, #{buId}
, #{createdDate}
, #{createdBy}
, #{updatedDate}
, #{updatedBy}
)

mapper.update:
UPDATE tb_wf_task
SET 
	TASK_NAME=#{taskName}
	, DESCRIPTION=#{description}
	, STATUS=#{status}
	, PRIORITY=#{priority}
	, START_DATE=#{startDate}
	, END_DATE=#{EndDate}
	, BU_ID=#{buId}
	, UPDATED_DATE=#{updatedDate}
	, UPDATED_BY=#{updatedBy}
WHERE TASK_ID=#{taskId}
