mapper.select:
SELECT template.`id`, template.`ATT_ID`, template.`MODULE`, template.`TEMPLATE_NAME`, template.`STATUS_CD`, cb2.CODE_NAME as statusName, template.`DESCRIPTION`, template.`BU_ID`, template.`CREATED_DATE`, template.`CREATED_BY`, template.`UPDATED_DATE`, template.`UPDATED_BY`, CONCAT(COALESCE(u1.FIRST_NAME,''), ' ',COALESCE(u1.LAST_NAME,'')) AS createdByName, CONCAT(COALESCE(u2.FIRST_NAME,''), ' ',COALESCE( u2.LAST_NAME,'')) AS updatedByName, B.FILE_PATH, B.FILE_NAME, B.FILE_EXTENSION, B.FILE_SIZE
FROM `tb_email_template` template INNER JOIN TB_ATT B ON template.ATT_ID = B.ATT_ID LEFT OUTER JOIN tb_codebook cb2 ON template.STATUS_CD = cb2.CODE_ID AND cb2.CODE_TYPE = 'ACTIVE_FLAG' LEFT JOIN TB_USER u1 ON template.CREATED_BY = u1.ID LEFT JOIN TB_USER u2 ON template.UPDATED_BY = u2.ID
AND template.module LIKE #{module}
AND template.TEMPLATE_NAME LIKE #{templateName}
AND template.STATUS_CD = #{statusCd}
ORDER BY template.ID ASC

mapper.update:
UPDATE tb_email_template
SET
 MODULE = #{module}
,TEMPLATE_NAME = #{templateName}
,STATUS_CD = #{statusCd}
,DESCRIPTION = #{description}
,ATT_ID = #{attId}
,UPDATED_BY = #{updatedBy}
,UPDATED_DATE = NOW()
WHERE ID = #{id}
