mapper.select:
select ur.room_id as roomId ,r.room_name as roomName ,ur.joined_at as createdAt ,(select count(*) from tb_msg_user_rooms subr where subr.room_id=r.room_id) as totalMember from tb_msg_user_rooms ur join tb_msg_rooms r on ur.room_id = r.room_id where ur.user_id =#{currentUserId}
and r.room_name like #{bRoomName}
order by r.room_name

mapper.insert:
INSERT INTO tb_msg_rooms
(room_name, created_at,CREATED_DATE,CREATED_BY)
VALUES(#{roomName}, now(),now(),#{currentUserId});

INSERT INTO tb_msg_user_rooms(user_id, room_id, joined_at)
values
(
	#{userId}
	, #{roomId}
	, now()
)

mapper.update:
UPDATE tb_msg_user_status
SET is_online=#{isOnline}, last_seen=now()
WHERE user_id=#{userId};

mapper.select:
SELECT user_id, is_online, last_seen
FROM tb_msg_user_status
WHERE user_id=#{userId}
