mapper.insert:
    	INSERT INTO tb_wf_business_rule
		(
		 WORKFLOW_ID
		, CONDITION_TYPE
		, CONDITION_VALUE_1
		, CONDITION_VALUE_2
		, PRIORITY
		, STATUS
		, BU_ID
		, CREATED_DATE
		, CREATED_BY
		, UPDATED_DATE
		, UPDATED_BY
		, SYSTEM_ID
		)
		VALUES(
		 #{workflowId}
		, #{conditionType}
		, #{conditionValue1}
		, #{conditionValue2}
		, #{priority}
		, #{status}
		, #{buId}
		, now()
		, #{createdBy}
		, now()
		, #{updatedBy}
		, #{systemId}
		)

mapper.update:
    	UPDATE tb_wf_business_rule
		SET 
		 CONDITION_TYPE=#{conditionType}
		, CONDITION_VALUE_1=#{conditionValue1}
		, CONDITION_VALUE_2=#{conditionValue2}
		, PRIORITY=#{priority}
		, STATUS=#{status}
		, BU_ID=#{buId}
		, UPDATED_DATE=now()
		, UPDATED_BY=#{updatedBy}
		, SYSTEM_ID=#{systemId}
		WHERE RULE_ID=#{ruleId}
